import { useMediaQuery } from "@material-ui/core";
import styled from "styled-components"
import Chip from '@material-ui/core/Chip';
import { makeStyles } from "@material-ui/core";
import Countdown from 'react-countdown';
import MonetizationOnIcon from '@material-ui/icons/MonetizationOn';

const useStyles = makeStyles({
    chipTime: {
        backgroundColor: "#ECF5FE",
        color: '#035263',
    },
    chipMoney: {
        backgroundColor: "#E8EFE6",
        color: '#035263',
    },
    button: {
        fontSize: '10px',
        width: '100%'
    }
  });

const Container = styled.div`
    width: 100%;
    display: grid;
    grid-template-columns: ${props => props.mobile ? 116 : 140}px auto;
    grid-template-rows: auto auto 24px 36px;
`

const Image = styled.div`
    grid-column-start: 1;
    grid-column-end: 1;
    grid-row-start: 1;
    grid-row-end: 5;
    height: ${props => props.mobile ? 116 : 140}px;
    background-image: url(${props => props.image});
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
`

const Title = styled.div`
    grid-column-start: 2;
    grid-column-end: 3;
    grid-row-start: 1;
    grid-row-end: 2;
    font-size: 18px;
    font-weight: bold;
`

const Money = styled(Chip)`
    background-color: #ffffff;
`



export default function ProductSearchView() {
    const matches = useMediaQuery('(max-width:600px)');
    const customStyle = useStyles();

    return <Container mobile={matches}>
        <Image mobile={matches} image={'https://images-na.ssl-images-amazon.com/images/I/81I-G29wTjL._AC_SY879_.jpg'} />
        <Title>Audifonos que son duros</Title>
        <div><Chip classes={{ root: customStyle.chipTime }} icon={<MonetizationOnIcon />} label={<Countdown date={Date.now() + 1000000} />} color="primary" /></div>
        <div><Money classes={{ root: customStyle.chipMoney }} icon={<MonetizationOnIcon />} label={'$36.99'} color="primary" /></div>
    </Container>
}
