import styled from 'styled-components'
import { useState } from 'react'
import Slider from "react-slick";
import ItemQuickView from './itemQuickView';
import useMediaQuery from '@material-ui/core/useMediaQuery';

const SelectionTab = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
`

const Tab = styled.h4`
    padding: 0 8px;
    margin-bottom: 0;
    transition: .5s;
    cursor: pointer;
    color: black;
`

export default function RecentAuctions() {

    const matches = useMediaQuery('(max-width:600px)');
    const [tabSelection, setTabSelection] = useState(true);

    var settings = {
        autoplay: true,
        autoplaySpeed: 5000,
        focusOnSelect: true,
        centerMode: true,
        dots: true,
        infinite: true,
        slidesToShow: matches ? 1 : 3,
        slidesToScroll: 1
    };

    const Container = styled.div`
        overflow-x: hidden;
    `

    return (
        <Container>
            <SelectionTab>
                <Tab selected={tabSelection} onClick={() => {setTabSelection(true)}}>Ending soon</Tab>
                <Tab selected={!tabSelection} onClick={() => {setTabSelection(false)}}>Newly added</Tab>
            </SelectionTab>
            <Slider style={{marginTop: '24px', marginBottom: '150px'}} {...settings}>
                <ItemQuickView/>
                <ItemQuickView/>
                <ItemQuickView/>
                <ItemQuickView/>
                <ItemQuickView/>
                <ItemQuickView/>
                <ItemQuickView/>
            </Slider>
        </Container>
    )
}
