import styled from "styled-components"

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    margin-bottom: 150px;
    position: relative;
`

const CategoriesHolder = styled.div`
    display: flex;
    width: 100%;
    padding: 16px;
    flex-wrap: wrap;
    justify-content: center;
    justify-items: center;
    align-items: flex-start;
    align-content: center;
    flex-direction: row;
`

const Category = styled.div`
    width: 25%;
    margin: 8px;
    height: ${props => props.height || '160px'};
    display: flex;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    box-shadow: 0 3px 10px 2px rgba(0, 0, 0, .05);
    border-radius: 8px;
    position: relative;
    background-color: ${props => props.backgroundColor || 'white'};
    cursor: pointer;
    :after {
        content: '${props => props.label}';
        color: ${props => props.color || 'black'};
        position: absolute;
        bottom: 8px;
        left: 0;
        right: 0;
        margin: 0 auto;
        text-align: center;
    }
`

const ImageHolder = styled.img`
width: 50%;
`

export default function CategoriesShowcase() {
    return (
        <Container>
            <h3 style={{ marginTop: 0 }}>Explore our various categories</h3>
            <CategoriesHolder>
                <Category label={'Electronics'}><ImageHolder  src="/loudspeaker.png"/></Category>
                <Category label={'Houses'}><ImageHolder src="/address.png" /></Category>
                <Category label={'Cars'}><ImageHolder src="/car.png" /></Category>
                <Category label={'Cloathing'}><ImageHolder src="/dress.png"/></Category>
                <Category label={'Furnitures'}><ImageHolder src="/sofa.png"/></Category>
                <Category label={'Apliances'}><ImageHolder src="/washing-machine.png"/></Category>
            </CategoriesHolder>
        </Container>
    )
}
