import styled from "styled-components"

const Container = styled.div`
    width: 100%;
    max-width: 280px;
    height: 280px;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    margin: 0 auto;
    transition: .5s;
    overflow: show;

    @media (max-width: 600px) {
        width: 90%;
        max-width: 240px;
        height: 260px;
    }
`

const ContentContainer = styled.div`
    margin: 15% 15%;
    width: 70%;
    height: 70%;
    overflow: show;
    background-color: white;
    border-radius: 8px;
    padding: 8px;
    box-shadow: 0 3px 10px 2px rgba(0, 0, 0, .05);
    transition: 1s;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;

    @media (max-width: 600px) {
        margin: 15% 5%;
        width: 95%;
        height: 70%;
    }
`

export default function ItemQuickView() {
    return <Container>
        <ContentContainer>
            <img src="https://images-na.ssl-images-amazon.com/images/I/81I-G29wTjL._AC_SY879_.jpg"/>
        </ContentContainer>
    </Container>
}
