import styled from "styled-components"
import Api from "./api";
import PersonIcon from '@material-ui/icons/Person';
import Button from '@material-ui/core/Button'
import { makeStyles } from '@material-ui/core/styles';
import IconButton from '@material-ui/core/IconButton';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import StoreIcon from '@material-ui/icons/Store';
import GavelIcon from '@material-ui/icons/Gavel';
import ChatIcon from '@material-ui/icons/Chat';
import Link from "next/link";

const useStyles = makeStyles({
    iconButton: {
      boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .1)',
      transition: '0.5s',
      '&:active': {
          boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0)'
      }
    },
    button: {
        height: '36px',
        fontWeight: "400",
        textTransform: 'none',
        borderRadius: '4px',
        boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .1)',
        transition: '0.5s',
        '&:active': {
            boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0)'
        }
    }
  });

const Container = styled.div`
    position: absolute;
    width: 100%;
    top: 0;
    height: 72px;
    overflow: hidden;
    display: flex;
    justify-content: center;
    flex-direction: row;
    padding: 12px;
    background: white;
`

const Menu = styled.div`
    height: 100%;
    overflow: show;
    display: flex;
    justify-content: space-between;
    align-content: center;
    align-items: center;
    justify-items: center;
    flex-direction: row;
    width: 100%;
    max-width: 768px;
`

const Tabs = styled.div`
    display: table;
    font-weight: 200;
    font-size: medium;
`

const Tab = styled.div`
    color: #024150;
    padding: 8px;
    background-color: ${(props) => {return props.selected && props.removeLetters ? '#EBFBFE' : 'white';}};
    padding-right: 12px;
    padding-left: 12px;
    text-align: center;
    display: table-cell;
    vertical-align: middle;
    cursor: pointer;
    font-weight: ${(props) => {return props.selected ? '400' : '200';}};
    ::before {
        content: "● ";
        font-size: 8px;
        display: ${(props) => {return props.selected && !props.removeLetters ? 'initial' : 'none';}};
    }
`

export function MenuBar({children, Selection}) {

    const matches = useMediaQuery('(max-width:600px)');

    let userSection;
    if (Api.userLoggedIn()) {
        userSection = profile()
    } else {
        userSection = signIn()
    }

    return <>
    <link href="https://fonts.googleapis.com/css2?family=Raleway&display=swap" rel="stylesheet"></link>
    <Container>
        <Menu>
            <Link href="/"><h3 style={{ fontFamily: "'Raleway', sans-serif", color: "#035263" }}>GoPostor</h3></Link>

            { matches ?
                userSection
                : 
                <Tabs>
                    <Tab selected={true}>Marketplace</Tab>
                    <Tab>My auctions</Tab>
                    {userSection}
                </Tabs>
            }
        </Menu>
    </Container>
    {children}
    { BottomPhoneMenu() }
    </>
}

export function BottomPhoneMenu() {

    const matches = useMediaQuery('(max-width:600px)');

    return <>
        { matches ?
            <Tabs style={{
                backgroundColor: "white",
                position: "absolute",
                bottom: "0",
                width: "100%",
                height: '56px', 
                alignContent: 'center',
                alignItems: 'center',
                justifyItems: 'center',
                boxShadow: '0px -3px 5px 2px rgba(0, 0, 0, .1)'
            }}>
                <Tab selected={true} removeLetters><StoreIcon/><br/><span style={{ fontSize: "small" }}>Marketplace</span></Tab>
                <Tab removeLetters><GavelIcon /><br/><span style={{ fontSize: "small" }}>My auctions</span></Tab>
            </Tabs>
            :
            <></>
        }
    </>

}

function profile() {
    const classes = useStyles();
    return <IconButton classes={{root: classes.iconButton}} color="primary">
         <PersonIcon />
    </IconButton>
}

function signIn() {
    const classes = useStyles();
    return <Button classes={{root: classes.button }} color="primary">
        Login
    </Button>
}
