import styled from 'styled-components'
import SearchIcon from '@material-ui/icons/Search';
import { useState } from 'react'

const SearchButton = styled.div`
    width: 44px;
    height: 48px;
    background-color: white;
    margin-left: 4px;
    border-radius: 4px;
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .1);
    display: flex;
    justify-content: center;
    justify-items: center;
    align-content: center;
    align-items: center;
    transition: 0.5s;
    :hover {
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .2)
    }
    :active {
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, 0);
    }
`

const SearchBarContainer = styled.div`
  width: 100%;
  height: 48px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`

const SearchInput = styled.input`
  width: calc(100% - 48px);
  background-color: #ffffff;
  outline: none;
  border: none;
  height: 100%;
  font-family: raleway, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  padding-left: 8px;
  font-size: 16px;
  font-weight: 100;
  box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .1);
  border-radius: 4px;
`

const RemoveText = styled.div`
  width: 14px;
  height: 14px;
  border-radius: 7px;
  right: 56px;
  top: 0;
  bottom: 0;
  line-height: 14px;
  background-color: lightgray;
  color: gray;
  position: absolute;
  text-align: center;
  vertical-align: middle;
  margin: auto;
  cursor: pointer;
  display: ${ props => props.haveText ? 'show' : 'none' };
`

const SearchSection = styled.div`  position: absolute;
  top: 52px;
  right: 0;
  left: 0;
  width: 75%;
  max-width: 450px;
  margin: auto;
  display: flex;
  flex-direction: column;
  overflow: visible;
`

const SearchResults = styled.div`
  background-color: white;
  border-radius: 8px;
  overflow: hidden;
  width: 100%;
  height: 20px;
`

export default function SearchBar(props) {
    const [search, setSearch] = useState("");

    return (
      <SearchSection {...props}>
        <SearchBarContainer>
          <SearchInput value={search} onChange={(t) => setSearch(t.target.value)} placeholder="Search..."/>
          <SearchButton>
              <SearchIcon />
          </SearchButton>
          <RemoveText align="center"  haveText={search.length > 0} onClick={() => setSearch('')}>Ã—</RemoveText>
        </SearchBarContainer>
        <SearchResults />
      </SearchSection>
    )
}
