export default function SearchBar(props) {
    const [search, setSearch] = useState("");
    const [results, setResults] = useState([]);
    const [currentNode, setCurrentNode] = useState(0);
    const happyPress = useKeyPress('ArrowUp');

    if (happyPress) {
      setCurrentNode(currentNode + 1);
      console.log(currentNode)
    }

    return (
      <SearchSection {...props}>
        <SearchBarContainer>
          <SearchInput ref={searchField} value={search} onChange={(t) => handleOnChnage(t.target.value, setSearch, setResults)} placeholder="Search for anything"/>
          <SearchButton>
              <SearchIcon />
          </SearchButton>
          <RemoveText align="center"  haveText={search.length > 0} onClick={() => handleOnChnage('', setSearch, setResults)}>Ã—</RemoveText>
        </SearchBarContainer>
        <SearchResults hide={results.length == 0} height={results.length * 52} >
          {happyPress && 'ðŸ˜Š'}
          {results.map((r) => <SearchResult category={'category'}>{r}</SearchResult>)}
        </SearchResults>
      </SearchSection>
    )
}

function handleOnChnage(value, searchSetter, resultSetter) {
  searchSetter(value)
  if(value.length == 0) {
    resultSetter([])
  } else {
    resultSetter(fetchResults(value))
  }
  if (searchField.current != null) {
    searchField.current.focus();
  }
}

function fetchResults(value) {
  return ["Test result 1", "Test result 2", "Test result 3", "Test result 4", "Test result 5", "Test result 6", "Test result 7", "Test result 8"]
}

function useKeyPress(targetKey) {
  // State for keeping track of whether key is pressed
  const [keyPressed, setKeyPressed] = useState(false);

  // If pressed key is our target key then set to true
  function downHandler({ key }) {
    if (key === targetKey) {
      setKeyPressed(true);
    }
  }

  // If released key is our target key then set to false
  const upHandler = ({ key }) => {
    if (key === targetKey) {
      setKeyPressed(false);
    }
  };

  // Add event listeners
  useEffect(() => {
    window.addEventListener('keydown', downHandler);
    window.addEventListener('keyup', upHandler);
    // Remove event listeners on cleanup
    return () => {
      window.removeEventListener('keydown', downHandler);
      window.removeEventListener('keyup', upHandler);
    };
  }, []); // Empty array ensures that effect is only run on mount and unmount

  return keyPressed;
}
