    font-size: 56px;
    margin: 32px;
    max-width: 480px;
    background: #ffffff;
    padding: 8px;
    border-radius: 8px;
    @media (max-width: 600px) {
        font-size: 40px;
        margin-top: 96px;
    }
`

const AccentedText = styled.span`
    color: #06AED5;
`

