import styled from "styled-components"
import Chip from '@material-ui/core/Chip';
import { Button, makeStyles } from "@material-ui/core";

const useStyles = makeStyles({
    chip: {
        position: "absolute",
        backgroundColor: "#ECF5FE",
        color: '#035263',
        ["@media (max-width:600px)"]: {
            fontSize: '10px',
            height: '22px'
        }
    }
  });

const Container = styled.div`
    width: 100%;
    max-width: 280px;
    height: 280px;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    margin: 0 auto;
    transition: .5s;
    overflow: show;

    @media (max-width: 600px) {
        width: 90%;
        max-width: 240px;
        height: 260px;
    }
`

const ContentContainer = styled.div`
    margin: 15% 15%;
    width: 70%;
    height: 70%;
    overflow: show;
    background-color: white;
    border-radius: 8px;
    padding: 8px;
    box-shadow: 0 3px 10px 2px rgba(0, 0, 0, .05);
    transition: 1s;
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    position: relative;

    @media (max-width: 600px) {
        margin: 15% 5%;
        width: 95%;
        height: 70%;
    }
`

const QuickImage = styled.div`
    width: 70%;
    height: 70%;
    background-image: url(${props => props.src});
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
`

export default function ItemQuickView() {

    const customStyle = useStyles()

    return <Container>
        <ContentContainer id="content_container">
            <Chip classes={{root: customStyle.chip}} label={<Countdown date={itemAuctionEndDate} />} color="primary" />
            <QuickImage src="https://images-na.ssl-images-amazon.com/images/I/81I-G29wTjL._AC_SY879_.jpg"/>
        </ContentContainer>
    </Container>
}
