import styled from "styled-components"
import Api from "./api"

const TrendyAuctionContainer = styled.div`
    display: flex;
    flex-direction: row;
`

const TrendyImage = styled.img`
    width: 50%;
    @media (max-width: 600px) {
        width: 100%;
    }
`

export default function TrendyAuction() {
    let trendyItem = fetchTrendyItem() 

    return <TrendyAuctionContainer>
        <TrendyImage src={trendyItem.images[0]} />
    </TrendyAuctionContainer>
}

async function fetchTrendyItem() {
    Api.axiosInstance.get(`${process.env.NEXT_PUBLIC_API_URL}/products/trendy`).then(function (response) {
        return(response.data);
    }).catch(function (error) {
        console.log(error)
    });
    return undefined;
}
