import styled from 'styled-components'
import SearchIcon from '@material-ui/icons/Search';
import { useState } from 'react'

const SearchButton = styled.div`
    width: 44px;
    height: 48px;
    background-color: white;
    margin-left: 4px;
    border-radius: 4px;
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .1);
    display: flex;
    justify-content: center;
    justify-items: center;
    align-content: center;
    align-items: center;
    transition: 0.5s;
    :hover {
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .2)
    }
    :active {
    box-shadow: 0 3px 5px 2px rgba(0, 0, 0, 0);
    }
`

const SearchBarContainer = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  width: 75%;
  max-width: 450px;
  margin: auto;
  height: 48px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`

const SearchInput = styled.input`
  width: calc(100% - 48px);
  background-color: #ffffff;
  outline: none;
  border: none;
  height: 100%;
  font-family: raleway, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  padding-left: 8px;
  font-size: 16px;
  font-weight: 100;
  box-shadow: 0 3px 5px 2px rgba(0, 0, 0, .1);
  border-radius: 4px;
`

const RemoveText = styled.div`
  width: 14px;
  height: 14px;
  text-align: center;
  font-size: 24px;
  border-radius: 50%;
  position: absolute;
  color: #00000060;
  right: 56px;
  top: 0;
  bottom: 0;
  text-align: center;
  vertical-align: middle;
  line-height: 14px;  
  margin: auto;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  align-content: center;
  justify-content: center;
  justify-items: center;
  display: ${ props => props.haveText ? 'show' : 'none' };
`

export default function SearchBar() {
    const [search, setSearch] = useState("");

    return (
        <SearchBarContainer>
            <SearchInput value={search} onChange={(t) => setSearch(t.target.value)} placeholder="Search..."/>
            <SearchButton>
                <SearchIcon />
            </SearchButton>
            <RemoveText align="center"  haveText={search.length > 0} onClick={() => setSearch('')}>Ã—</RemoveText>
        </SearchBarContainer>
    )
}
