import styled from "styled-components"

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    margin-bottom: 150px;
    position: relative;
`

const CategoriesHolder = styled.div`
    display: flex;
    width: 100%;
    padding: 16px;
    flex-wrap: wrap;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    flex-direction: row;
`

const Category = styled.div`
    width: 30%;
    height: 200px;
    display: flex;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
`

export default function CategoriesShowcase() {
    return (
        <Container>
            <h3 style={{ marginTop: 0 }}>Explore our various categories</h3>
            <CategoriesHolder>

            </CategoriesHolder>
        </Container>
    )
}
