import styled from "styled-components"
import Typist from 'react-typist';

const Container = styled.div`
position: relative;
`

const Title = styled(Typist)`
    font-size: 64px;
    margin: 32px;
`

const AccentedText = styled.span`
    color: orange;
`

export default function Presentation() {
    return (
        <Container>
            <Title startDelay={1000} cursor={{
                show: true,
                blink: true,
                element: '|',
                hideWhenDone: false,
            }}>
                I want to <AccentedText>auction</AccentedText>
                <Typist.Delay ms={500} />
                <br />
                my car
                <Typist.Backspace count={3} delay={200} />
                apartament
                <Typist.Backspace count={10} delay={400} />
                laptop
                <Typist.Backspace count={26} delay={600} />
                What do <AccentedText>you</AccentedText> want to <AccentedText>auction</AccentedText>?
            </Title>
        </Container>
    )
}
