import { useEffect, useState } from "react"
import styled from "styled-components"
import Api from "./api"
import Chip from '@material-ui/core/Chip';
import { makeStyles } from "@material-ui/core";
import Countdown from 'react-countdown';


var percentColors = [
    { pct: 0.0, color: { r: 0x69, g: 0x99, b: 0x5D } },
    { pct: 0.5, color: { r: 0xff, g: 0xBC, b: 0x1F } },
    { pct: 1.0, color: { r: 0xEE, g: 0x6C, b: 0x4D } }];

var getColorForPercentage = function(pct) {
    for (var i = 1; i < percentColors.length - 1; i++) {
        if (pct < percentColors[i].pct) {
            break;
        }
    }
    var lower = percentColors[i - 1];
    var upper = percentColors[i];
    var range = upper.pct - lower.pct;
    var rangePct = (pct - lower.pct) / range;
    var pctLower = 1 - rangePct;
    var pctUpper = rangePct;
    var color = {
        r: Math.floor(lower.color.r * pctLower + upper.color.r * pctUpper),
        g: Math.floor(lower.color.g * pctLower + upper.color.g * pctUpper),
        b: Math.floor(lower.color.b * pctLower + upper.color.b * pctUpper)
    };
    return 'rgb(' + [color.r, color.g, color.b].join(',') + ')';
    // or output as hex if preferred
};

const useStyles = makeStyles({
    chip: {
        backgroundColor: getColorForPercentage(.70)
    }
  });

const Main = styled.main`
  display: flex;
  justify-content: center;
`

const TrendyAuctionContainer = styled.div`
    width: 100%;
    margin-bottom: 160px;
    display: flex;
    flex-direction: row;
    max-width: 768px;
    justify-content: center;
    justify-items: center;
    @media (max-width: 600px) {
        flex-direction: column;
    }
`

const TrendyImageContainer = styled.div`
    display: flex;
    flex-direction: row;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    width: 50%;
    height: 400px;
    @media (max-width: 600px) {
        width: 100%;
        height: 300px;
    }
`

const TrendyImage = styled.img`
    height: 88%;
    @media (max-width: 600px) {
        height: 92%;
    }
`

const TrendyAuctionDetail = styled.div`
    margin: 0 16px;
    width: 50%;
    @media (max-width: 600px) {
        width: 100%;
    }
`

const Title = styled.h3`
    font-weight: 200;
`

export default function TrendyAuction() {
    const [itemImage, setItemImage] = useState('')
    const [itemName, setItemName] = useState('')
    const [itemAuctionEndDate, setItemAuctionEndDate] = useState(Date.now())

    useEffect(() => {
        fetchTrendyItem((trendyItem) => {
            setItemImage(trendyItem.images[0]) 
            setItemName(trendyItem.title)
            setItemAuctionEndDate(trendyItem.auction_closes_at)
        })
    }, []);

    const customStyle = useStyles()

    return (
        <Main>
            <TrendyAuctionContainer>
                <TrendyImageContainer>
                    <TrendyImage src={itemImage} />
                </TrendyImageContainer>
                <TrendyAuctionDetail>
                    <Title>{itemName}</Title>
                    <Chip classes={{root: customStyle.chip}} label={<Countdown date={itemAuctionEndDate} />} color="primary" />
                </TrendyAuctionDetail>
            </TrendyAuctionContainer>
        </Main>
    )
}

async function fetchTrendyItem(onSuccess) {
    await Api.axiosInstance.get(`${process.env.NEXT_PUBLIC_API_URL}/products/trendy`).then(function (response) {
        onSuccess(response.data);
    }).catch(function (error) {
        console.log(error)
    });
}
