import styled from "styled-components"

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    margin-bottom: 150px;
    position: relative;
`

const CategoriesHolder = styled.div`
    display: flex;
    width: 100%;
    padding: 16px;
    flex-wrap: wrap;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    flex-direction: row;
`

const Category = styled.div`
    width: 25%;
    margin: 8px;
    height: 160px;
    display: flex;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    box-shadow: 0 3px 10px 2px rgba(0, 0, 0, .05);
    border-radius: 8px;
    :after {
        content: '${props => props.label}'
    }
`

export default function CategoriesShowcase() {
    return (
        <Container>
            <h3 style={{ marginTop: 0 }}>Explore our various categories</h3>
            <CategoriesHolder>
                <Category label={electronics}>1</Category>
                <Category>2</Category>
                <Category>3</Category>
                <Category>4</Category>
                <Category>5</Category>
                <Category>6</Category>
            </CategoriesHolder>
        </Container>
    )
}
