import styled from "styled-components"

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: relative;
    background-color: #ECF5FE;
    padding: 32px;
`

const InfoConatiner = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 90%;
    max-width: 765px;
`

const Info = styled.div`
    display: flex;
    justify-content: center;
    justify-items: center;
    align-items: center;
    align-content: center;
    width: ${props => props.width || '50%'};
    height: 240px;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;
    background-image: url(${props => props.src || ''});
`

export default function CanDo() {
    return (
        <Container>
            <h3>With GoPostor you can</h3>
            <InfoConatiner>
                <Info width={'60%'} src={'/undraw_best_place.svg'}/>
                <Info width={'40%'}><h3>Participate in live auctions</h3>Compete in an hour long private event in which you can make multiple offers toping th previews one</Info>
                <Info width={'40%'}>Paticipate in proxy bidding</Info>
                <Info width={'60%'} src={'/undraw_sharing_articles.svg'}/>
            </InfoConatiner>
        </Container>
    )
}
